import React, { useState, useEffect } from "react";

function EquipmentSelect({ onSelect, onClose }) {
  const [equipment, setEquipment] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);

  // Получаем список оборудования при открытии
  useEffect(() => {
    fetch("/api/inventory")
      .then((res) => res.json())
      .then((data) => setEquipment(data))
      .catch((err) => console.error("Ошибка загрузки оборудования:", err));
  }, []);

  // Обработка выбора оборудования
  const toggleSelection = (item) => {
    setSelectedItems((prev) =>
      prev.includes(item)
        ? prev.filter((i) => i !== item)
        : [...prev, item]
    );
  };

  // Подтверждение выбора
  const confirmSelection = () => {
    onSelect(selectedItems);
    onClose(); // Закрываем окно после выбора
  };

  return (
    <div className="equipment-select-modal">
      <h3>Выберите оборудование</h3>
      <ul>
        {equipment.map((item) => (
          <li key={item.id} onClick={() => toggleSelection(item.name)}>
            <input
              type="checkbox"
              checked={selectedItems.includes(item.name)}
              readOnly
            />
            {item.name}
          </li>
        ))}
      </ul>
      <button onClick={confirmSelection}>Подтвердить</button>
      <button onClick={onClose}>Закрыть</button>
    </div>
  );
}

export default EquipmentSelect;
