                            onBlur={handleBlur}
                        />
                        <div>---</div>
                        –ü–æ <input
                            type="datetime-local"
                            name="end_date"
                            value={rental.end_date ? new Date(rental.end_date).toISOString().slice(0, 16) : ""}
                            onChange={handleChange}
