import React, { useState, useEffect } from 'react';
import './Inventory.css';

const InventoryTable = () => {
    const [inventory, setInventory] = useState([]);
    const [loading, setLoading] = useState(true);
    const [sortColumn, setSortColumn] = useState('id'); // Текущий столбец для сортировки
    const [sortOrder, setSortOrder] = useState('asc'); // Порядок сортировки (asc/desc)
    const [editingItem, setEditingItem] = useState(null); // Для редактирования
    const [updatedData, setUpdatedData] = useState({}); // Для отслеживания обновленных данных
    const [notifications, setNotifications] = useState([]); // Для уведомлений
    const [isProcessing, setIsProcessing] = useState(false); // Флаг, чтобы избежать отправки на бэкенд несколько раз

    // Загрузка данных с сервера
    useEffect(() => {
        const fetchInventory = async () => {
            try {
                const response = await fetch("http://localhost:5000/api/inventory");
                if (!response.ok) {
                    throw new Error(`Ошибка загрузки: ${response.status}`);
                }
                const data = await response.json();
                setInventory(data);
            } catch (error) {
                console.error('Ошибка загрузки инвентаря:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchInventory();
    }, []);

    // Обработчик изменения данных в таблице
    const handleInputChange = (e, field, id) => {
        const value = e.target.value;

        setInventory(prevInventory =>
            prevInventory.map(item =>
                item.id === id ? { ...item, [field]: value } : item
            )
        );

        setUpdatedData(prev => ({
            ...prev,
            [id]: {
                ...prev[id],
                [field]: value,
            },
        }));
    };

    // Сохранение изменений
    const handleSave = async (id) => {
        if (isProcessing) return; // Блокируем несколько отправок данных

        setIsProcessing(true); // Устанавливаем флаг обработки

        const updatedItem = inventory.find((item) => item.id === id);
        const updatedFields = updatedData[id] || {};

        const updatedDataForAPI = { ...updatedItem, ...updatedFields };
        const hasChanges = Object.keys(updatedFields).length > 0;

        if (!hasChanges) {
            setIsProcessing(false);
            return;
        }

        try {
            const response = await fetch(`http://localhost:5000/api/inventory/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updatedDataForAPI),
            });

            if (response.ok) {
                const updatedField = Object.keys(updatedFields)[0]; // Берем первый измененный столбец
                const newValue = updatedFields[updatedField];

                // Добавляем уведомление
                setNotifications(prevNotifications => {
                    const newNotification = {
                        message: `Инвентарь обновлен: Строка ${id}, столбец ${updatedField} теперь ${newValue}`,
                        type: "success",
                    };

                    // Добавляем новое уведомление в начало массива
                    const updatedNotifications = [newNotification, ...prevNotifications];

                    // Ограничиваем количество уведомлений до 4
                    if (updatedNotifications.length > 4) {
                        updatedNotifications.pop(); // Убираем старое уведомление, если их больше 4
                    }

                    return updatedNotifications;
                });

                setEditingItem(null); // Закрытие режима редактирования
            } else {
                setNotifications(prevNotifications => [
                    ...prevNotifications,
                    { message: "Ошибка при сохранении изменений", type: "error" }
                ]);
            }
        } catch (error) {
            console.error('Ошибка при обновлении инвентаря:', error);
            setNotifications(prevNotifications => [
                ...prevNotifications,
                { message: "Ошибка при сохранении изменений", type: "error" }
            ]);
        } finally {
            setIsProcessing(false); // Снимаем флаг обработки
        }
    };

    // Обработчик клика по строке для начала редактирования
    const handleClickEdit = (itemId) => {
        setEditingItem(itemId); // Устанавливаем редактируемую строку
    };

    // Сортировка
    const handleSort = (column) => {
        let newSortOrder = 'asc';
        if (sortColumn === column && sortOrder === 'asc') {
            newSortOrder = 'desc';
        }
        setSortColumn(column);
        setSortOrder(newSortOrder);

        const sortedData = [...inventory].sort((a, b) => {
            if (a[column] < b[column]) return newSortOrder === 'asc' ? -1 : 1;
            if (a[column] > b[column]) return newSortOrder === 'asc' ? 1 : -1;
            return 0;
        });
        setInventory(sortedData);
    };

    // Отображение строки инвентаря
    const renderRow = (item) => {
        if (editingItem === item.id) {
            return (
                <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>
                        <input
                            type="text"
                            value={item.name}
                            onChange={(e) => handleInputChange(e, 'name', item.id)}
                            onBlur={() => handleSave(item.id)}
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.category}
                            onChange={(e) => handleInputChange(e, 'category', item.id)}
                            onBlur={() => handleSave(item.id)}
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.subcategory}
                            onChange={(e) => handleInputChange(e, 'subcategory', item.id)}
                            onBlur={() => handleSave(item.id)}
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.model}
                            onChange={(e) => handleInputChange(e, 'model', item.id)}
                            onBlur={() => handleSave(item.id)}
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.serial_number}
                            onChange={(e) => handleInputChange(e, 'serial_number', item.id)}
                            onBlur={() => handleSave(item.id)}
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.notes}
                            onChange={(e) => handleInputChange(e, 'notes', item.id)}
                            onBlur={() => handleSave(item.id)}
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.status}
                            onChange={(e) => handleInputChange(e, 'status', item.id)}
                            onBlur={() => handleSave(item.id)}
                        />
                    </td>
                    <td>
                        <input
                            type="number"
                            value={item.total}
                            onChange={(e) => handleInputChange(e, 'total', item.id)}
                            onBlur={() => handleSave(item.id)}
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.belongs_to}
                            onChange={(e) => handleInputChange(e, 'belongs_to', item.id)}
                            onBlur={() => handleSave(item.id)}
                        />
                    </td>
                    <td>
                        <button onClick={() => handleSave(item.id)}>Сохранить</button>
                        <button onClick={() => setEditingItem(null)}>Отменить</button>
                    </td>
                </tr>
            );
        } else {
            return (
                <tr key={item.id} onClick={() => handleClickEdit(item.id)}>
                    <td>{item.id}</td>
                    <td>{item.name}</td>
                    <td>{item.category}</td>
                    <td>{item.subcategory}</td>
                    <td>{item.model}</td>
                    <td>{item.serial_number}</td>
                    <td>{item.notes}</td>
                    <td>{item.status}</td>
                    <td>{item.total}</td>
                    <td>{item.belongs_to}</td>
                    <td>
                        <button>Редактировать</button>
                    </td>
                </tr>
            );
        }
    };

    return (
        <div>
            <h2>Инвентарь</h2>
            {loading ? (
                <p>Загрузка...</p>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th onClick={() => handleSort('id')}>ID</th>
                            <th onClick={() => handleSort('name')}>Название</th>
                            <th onClick={() => handleSort('category')}>Категория</th>
                            <th onClick={() => handleSort('subcategory')}>Подкатегория</th>
                            <th onClick={() => handleSort('model')}>Модель</th>
                            <th onClick={() => handleSort('serial_number')}>Серийный номер</th>
                            <th onClick={() => handleSort('notes')}>Примечания</th>
                            <th onClick={() => handleSort('status')}>Статус</th>
                            <th onClick={() => handleSort('total')}>Общее количество</th>
                            <th onClick={() => handleSort('belongs_to')}>Кому принадлежит</th>
                            <th>Действия</th>
                        </tr>
                    </thead>
                    <tbody>
                        {inventory.map(renderRow)}
                    </tbody>
                </table>
            )}
            {notifications.length > 0 && (
                <div className="notifications-container">
                    {notifications.map((notification, index) => (
                        <div key={index} className={`notification ${notification.type}`}>
                            {notification.message}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default InventoryTable;
